import React from 'react'
import { useState } from 'react';
import {Images} from '../constants'

const NavBarItems = [
    {name: "Home"},
    {name: "Articles"},
    {name: "Pages"},
    {name: "Pricing"},
    {name: "FAQ"}
]

const NavListItem= ({item}) => {
    return (
<li className='relative group mx-1'>
    <a href="/" className='mx-1 text-lg group-hover:text-red-500'>{item}</a>
    <span 
    className='absolute opacity-0 top-0
               group-hover:right-[95%]
             group-hover:text-red-500
               group-hover:opacity-100
               transition-all duration-300'>
                /</span>
</li>);
}

const Header = () => {
    const [navBarVisible,setNavBar] = useState(false);
  return (
   <header className='container flex justify-between items-center p-1 mx-auto'>
    <div className='w-10 h-10'><img src={Images.Logo} alt="Logo"/></div>
    <div className={`${navBarVisible? "right-0":"-right-full" }flex items-center gap-x-9`}>
        <ul className='flex gap-x-5'>
            {NavBarItems.map((item) => {
                return (
                    <NavListItem key={item.name} item={item.name}/>
                );
            })}
        </ul>
        <button className='rounded-full border-2 border-blue-500 py-2 px-4 text-blue-500 hover:bg-blue-500 hover:text-white transition-all duration-300'>Sign Up</button>
    </div>
   </header>
  )
}

export default Header
